package com.example.myapplication.ui

import androidx.appcompat.app.AppCompatActivity
import android.os.Bundle
import android.view.View
import androidx.lifecycle.Observer
import androidx.lifecycle.ViewModelProviders
import androidx.recyclerview.widget.LinearLayoutManager
import com.example.myapplication.MainAdapter
import com.example.myapplication.viewmodel.MainViewModel
import com.example.myapplication.R
import com.facebook.shimmer.ShimmerFrameLayout
import kotlinx.android.synthetic.main.activity_main.*

class MainActivity : AppCompatActivity() {


    private lateinit var adapter: MainAdapter
    private val viewModel by lazy { ViewModelProviders.of(this).get(MainViewModel::class.java) }

    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        setContentView(R.layout.activity_main)

        adapter  = MainAdapter(this)

        rv.layoutManager = LinearLayoutManager(this)

        rv.adapter = adapter

        //val dummyList: MutableList<User> = mutableListOf<User>()
       // dummyList.add(User("data:image/jpeg;base64,","prueba","tutoriales par android studio"))
       // dummyList.add(User("data:image/jpeg;base64,","futbol","tutoriales par dklakdlsakl studio"))




        observeData()




    }

    fun observeData(){
        shimmer_view_container.startShimmer()

        viewModel.fetchUserData()  .observe(this, Observer {
            shimmer_view_container.hideShimmer()
            shimmer_view_container.stopShimmer()
            shimmer_view_container.visibility = View.GONE

            adapter.setListData(it)
            adapter.notifyDataSetChanged()
        })
    }

}
